mapper.select:
SELECT
t.*
FROM team t
WHERE t.owner = #{userId}
AND t.shop_id = #{shopId}
AND t.status = 0

SELECT
t.*
FROM team t
WHERE t.owner = #{userId}
AND t.chat_group_id = #{chatGroupId}
AND t.status = 0

SELECT t.id AS teamId, t.owner AS userId, u.user_name AS userName, u.cover_pic AS userCover, u.sex AS userSex, TIMESTAMPDIFF(YEAR,u.birthday,NOW()) AS userAge, t.activity_name AS activityName, t.start_time AS startTime, t.activity_address AS activityAddress, 1 AS distance, t.activity_type AS activityType, (SELECT s.price FROM shop s WHERE s.id = t.shop_id) AS price, t.pay_way AS payWay, (SELECT COUNT(tu.id) FROM team_users tu WHERE tu.team_id = t.id) AS joinUserNum, t.number AS totalUserNum, t.tag AS label, t.status as teamStatus FROM team t LEFT JOIN `user` u ON u.id = t.owner
WHERE t.owner = #{userId}
LEFT JOIN team_users tu ON tu.user_id = u.id WHERE tu.user_id = #{userId} AND t.owner != #{userId}
